{
  "name": "Pagination Pattern",
  "description": "Standard pagination implementation with cursor-based navigation",
  "example": {
    "typescript": "interface PaginatedResponse<T> {
  data: T[];
  hasNext: boolean;
  hasPrev: boolean;
  nextCursor?: string;
  prevCursor?: string;
}

function paginate<T>(
  items: T[],
  cursor?: string,
  limit: number = 20
): PaginatedResponse<T> {
  const startIndex = cursor ? parseInt(cursor) : 0;
  const endIndex = startIndex + limit;
  const data = items.slice(startIndex, endIndex);

  return {
    data,
    hasNext: endIndex < items.length,
    hasPrev: startIndex > 0,
