import { log } from '@shared/log';

function processUser(userId: string): void {
  log('info', 'user.process.start', { userId });

  try {
    // business logic
    const user = getUser(userId);
    log('info', 'user.process.success', { userId, userEmail: user.email });
  } catch (error) {
    log('error', 'user.process.failed', { userId, error: error.message });
    throw error;
  }
}
